script:
frappe.ui.form.on('Sales Invoice', {
refresh: function(frm) {
		set_filters(frm);
		get_mapped_income_account(frm);
		},

  sales_order_type(frm){
    get_mapped_income_account(frm);
  },
  validate(frm){
    get_mapped_income_account(frm);
    set_mapped_income_account(frm);
  }
});

function set_filters(frm){
	  frm.set_query('gst_account', () => {
		return{
		  filters: {
			is_group: 0,
			account_type: 'Tax'
		  }
		}
	  });
	}



function get_mapped_income_account(frm){
  if(frm.doc.customer){
    frappe.call({
      "method": "aj.custom.si.get_mapped_income_account",
      "args": {
        "customer": frm.doc.customer
      },
      callback: function(r){
        if(r){
          frm.set_value('mapped_income_account', r.message);
        }
      }
    });
  }
  else{
    frm.set_value('mapped_income_account', "");
  }
}

function set_mapped_income_account(frm){
  if(frm.doc.mapped_income_account){
    $.each(frm.doc.items || [],function(i,item){
      item.income_account = frm.doc.mapped_income_account;
		});
  }
}

